/* Procedure structure for procedure `update_match_style` */

/*!50003 DROP PROCEDURE IF EXISTS  `update_match_style` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `update_match_style`(IN matchid INT, IN matchstyle TINYINT, IN matchtempo TINYINT, IN matchintensity TINYINT, IN  matchdescription TEXT)
BEGIN
DECLARE STYLE_COL, TEMPO_COL, INTENSITY_COL VARCHAR(100);
SET STYLE_COL= CASE WHEN matchstyle=0 THEN 'style_off'
    WHEN matchstyle=1 THEN 'style_def'
    ELSE ''
    END;
SET TEMPO_COL= CASE WHEN matchtempo=0 THEN 'tempo_vs'
    WHEN matchtempo=1 THEN 'tempo_sl'
    WHEN matchtempo=2 THEN 'tempo_med'
    WHEN matchtempo=3 THEN 'tempo_fs'
    WHEN matchtempo=4 THEN 'tempo_vf'
    ELSE ''
    END;
SET INTENSITY_COL= CASE WHEN matchintensity=0 THEN 'intensity_vw'
    WHEN matchintensity=1 THEN 'intensity_wk'
    WHEN matchintensity=2 THEN 'intensity_med'
    WHEN matchintensity=3 THEN 'intensity_st'
    WHEN matchintensity=4 THEN 'intensity_vs'
    ELSE ''
    END;
INSERT INTO match_comments (`match_id`,`match_style`,`match_tempo`,`match_intensity`,`description`) VALUES(matchid,matchstyle,matchtempo,matchintensity,matchdescription);
SET @STR=CONCAT("update match_style SET ",STYLE_COL," = ",STYLE_COL,"+1,",TEMPO_COL," = ",TEMPO_COL,"+1,",INTENSITY_COL," = ",INTENSITY_COL,"+1 where match_id=",matchid);
SET @STR2=CONCAT("update match_style SET style_pts=style_def-style_off, tempo_pts=tempo_vs*(-2)-tempo_sl+tempo_fs+tempo_vf*2,intensity_pts=intensity_vw*(-2)-intensity_wk+intensity_st+intensity_vs*2");
PREPARE stmt FROM @STR;
EXECUTE stmt;
PREPARE stmt FROM @STR2;
EXECUTE stmt;
END */$$
DELIMITER ;

